create TABLE player 
 (ID NUMERIC(12), Name VARCHAR(50), FullName VARCHAR(50), Age NUMERIC(12), Height NUMERIC(12), Weight NUMERIC(12), PhotoUrl VARCHAR(50), Nationality VARCHAR(50), Overall NUMERIC(12), Potential NUMERIC(12), Growth NUMERIC(12), TotalStats NUMERIC(12), BaseStats NUMERIC(12), Positions VARCHAR(50), BestPosition VARCHAR(50), Club VARCHAR(50), ValueEUR NUMERIC(12), WageEUR NUMERIC(12), ReleaseClause NUMERIC(12), ClubPosition VARCHAR(50), ContractUntil NUMERIC(12), ClubNumber NUMERIC(12), ClubJoined NUMERIC(12), OnLoad BOOLEAN, NationalTeam VARCHAR(50), NationalPosition VARCHAR(50), NationalNumber NUMERIC(12), PreferredFoot VARCHAR(50), primary key (ID));
create TABLE team 
 (ID NUMERIC(12), Name VARCHAR(50), League VARCHAR(50), LeagueId NUMERIC(12), Overall NUMERIC(12), Attack NUMERIC(12), Midfield NUMERIC(12), Defence NUMERIC(12), TransferBudget NUMERIC(12), DomesticPrestige NUMERIC(12), IntPrestige NUMERIC(12), Players NUMERIC(12), StartingAverageAge NUMERIC(12, 3), AllTeamAverageAge NUMERIC(12, 3), primary key (ID));
